ReminDoo.getWeekDay = function(d) {
    var s = "";
    switch (d.getDay()) {
        case 0:
            s = "Sun";
            break;
        case 1:
            s = "Mon";
            break;
        case 2:
            s = "Tue";
            break;
        case 3:
            s = "Wed";
            break;
        case 4:
            s = "Thu";
            break;
        case 5:
            s = "Fri";
            break;
        case 6:
            s = "Sat";
            break;
    }
    return ReminDoo.T(s);
};
